import Unzer from '../../Unzer';
import Resources from './Resources';
import Payment from './Payment';
export default abstract class AbstractPayment {
    private _id;
    private _unzer;
    private _payment;
    private _payload;
    constructor(unzer: Unzer);
    /**
     * Set a payment object
     *
     * @param {Payment} payment
     */
    setPayment(payment: Payment): void;
    /**
     * Get payment object
     *
     * @returns {Payment}
     */
    getPayment(): Payment;
    /**
     * Get instance Unzer
     *
     * @returns {Unzer}
     */
    getUnzer(): Unzer;
    /**
     * Set Payment Id
     *
     * @param {string} paymentId
     */
    setId(paymentId: string): void;
    /**
     * Get Payment Id
     *
     * @returns {string}
     */
    getId(): string;
    /**
     * Get payload object
     *
     * @returns {*}
     */
    getPayload(): any;
    /**
     * Set payload object
     *
     * @param {*} payload
     * @returns
     */
    setPayload(payload: any): any;
    abstract getResources(): Resources;
}
